mapper.select:

select no,title,content,user_no userNo,groupNo,orderNo,depthNo
from replyboard
where no=#{no}

mapper.update:


update replyboard
set orderNo=orderNo+1
where groupNo=#{groupNo}
and orderNo=#{orderNo}

mapper.delete:

delete from replyboard
where groupNo=#{groupNo}
and orderNo>=#{orderNo}

mapper.update:

update replyboard
set title=#{title},
    content=#{content}
where no=#{no}
